mapper.select:
SELECT id, user_id, hashed_password, role, introduce
FROM user
WHERE user_id = #{userId}
AND withdrawal = 0
;

SELECT id, user_id, hashed_password, role, introduce
FROM user
WHERE id = #{id};

mapper.update:
UPDATE user
SET withdrawal = 1
WHERE id = #{id}

mapper.select:
SELECT user_id, introduce, created_at
FROM user
WHERE id = #{id}

mapper.update:
